import Link from 'next/link';
import { Router } from '../routes';
import { connect } from 'react-redux';
import { Popover, Icon } from 'antd';
import UserInfo from './UserInfo';
import axios from 'axios';
//import { setCurrentUser } from '../redux/actions/auth_actions';

import React, { Component } from 'react';

import '../styles.less';

const MUST_LOG_IN = [''];

class Toolbar extends Component {
    // updateUsers = async () => {
    //     try {
    //         axios.defaults.headers.common['Authorization'] = localStorage.getItem('jwtToken');
    //         const { data } = await axios({
    //             method: 'get',
    //             url: `${process.env.SERVER_URL}/api/auth/getUser`,
    //             data: {
    //                 id: this.props.user.id
    //             }
    //         });
    //         this.props.setCurrentUser(data);
    //     } catch (e) {}
    // };

    componentDidMount() {
        if (!this.props.isAuthenticated && MUST_LOG_IN.includes(this.props.page)) {
            Router.push('/notloggedin');
        }
    }

    renderLink(name, page, icon, href, onClick, noMargin) {
        return (
            <React.Fragment>
                <li
                    className="nav-item"
                    style={{ margin: `${noMargin ? '3px 0px' : '6px 25px'} 0px 0px` }}
                >
                    <Link href={href} passHref>
                        <a
                            className={this.props.page === page ? 'active' : 'inactive'}
                            onClick={onClick}
                            href={href}
                        >
                            <div
                                style={{
                                    display: 'flex',
                                    alignItems: 'center'
                                }}
                            >
                                <Icon
                                    type={icon}
                                    size={20}
                                    style={{
                                        marginRight: 5
                                    }}
                                />
                                {name}
                            </div>
                        </a>
                    </Link>
                </li>
                <style jsx>{`
                    a {
                        color: #000000;
                    }
                    a:hover {
                        color: #ff8282;
                        text-decoration: none;
                    }
                    .active {
                        color: #ff8282;
                        text-decoration: none;
                    }
                    ul {
                        list-style-type: none;
                    }
                `}</style>
            </React.Fragment>
        );
    }

    render() {
        return (
            <div>
                <React.Fragment>
                    {/* Render Toolbar */}
                    <nav
                        className="navbar navbar-expand-lg navbar-light bg-light"
                        style={styles.navBar}
                    >
                        <div
                            style={{
                                display: 'flex',
                                flexDirection: 'row',
                                alignItems: 'center'
                            }}
                        >
                            <a
                                className="navbar-brand"
                                style={{
                                    fontSize: '30px',
                                    margin: 0,
                                    color: '#569ab1',
                                    display: 'flex',
                                    flexDirection: 'row',
                                    alignItems: 'center'
                                }}
                                href="/"
                            >
                                <img
                                    src="/static/logo.png"
                                    style={{ height: '6vh', marginTop: '0vh' }}
                                />
                                &ensp;<div style={{ color: '#FF8282' }}>Invisible Ink</div>
                            </a>
                        </div>

                        <div
                            className="form-inline my-2 my-lg-0"
                            style={{
                                fontWeight: 'bold',
                                display: 'flex',
                                flexDirection: 'row',
                                alignItems: 'center'
                            }}
                        >
                            {/* Render collection of links */}
                            <ul
                                className="navbar-nav mr-auto"
                                style={{
                                    display: 'flex',
                                    flexDirection: 'row'
                                }}
                            >
                                {this.props.user.userType === 1
                                    ? this.renderLink('Explore', 'e', 'search', '/explore')
                                    : null}

                                {this.props.user.userType === 1 || this.props.user.userType === 0
                                    ? this.renderLink('My Cases', 'c', 'copy', '/cases')
                                    : null}

                                {this.renderLink('FAQ', 'q', 'question-circle', '/faq')}

                                <Popover
                                    content={<UserInfo />}
                                    placement="bottomRight"
                                    trigger="click"
                                    //onVisibleChange={this.updateUsers}
                                >
                                    <li
                                        className="nav-item"
                                        style={{
                                            margin: '6px 25px 0px 0px'
                                        }}
                                    >
                                        <a
                                            style={{
                                                fontWeight: 'regular',
                                                display: 'flex',
                                                alignItems: 'center'
                                            }}
                                        >
                                            <Icon
                                                type="user"
                                                size={20}
                                                style={{
                                                    marginRight: '5px'
                                                }}
                                            />
                                            Profile
                                        </a>
                                    </li>
                                </Popover>
                            </ul>
                        </div>
                    </nav>

                    {/* Legit CSS more powerful than inline react styles?? */}
                    <style jsx>{`
                        a {
                            color: #000000;
                        }
                        a:hover {
                            color: #ff8282;
                            text-decoration: none;
                        }
                        .active {
                            color: #ff8282;
                            text-decoration: none;
                        }
                        ul {
                            list-style-type: none;
                        }
                    `}</style>
                </React.Fragment>
            </div>
        );
    }
}

const styles = {
    navlink: {
        margin: '6px 25px 0px 0px'
    },
    navBar: {
        borderWidth: '0px',
        display: 'flex',
        flexDirection: 'row',
        justifyContent: 'space-between',
        padding: '10px',
        backgroundColor: '#FFFFFF'
    }
};

function mapStateToProps(state) {
    const { page, isAuthenticated, user } = state.auth;
    return { page, isAuthenticated, user };
}

export default connect(mapStateToProps, null)(Toolbar);
